    ],
    "marker": {
      "color": "#033663"
    },
    "mode": "lines+markers"
  }
}

function addYAxis(name){
  return {
    "title": {
      "text": name,
      "standoff": 6,
      "font": {
        "size": 12
      }
    },
    "titlefont": {color : "#033663"},
    "tickfont": {color : "#033663"},
    "type": "linear",
    "zerolinecolor": "#033663"
  }
}

