function updateMetric(updatedRow){

  const metricId = updatedRow["id"];
  const metricName = updatedRow["이름"];
  const modifiedMetricImportance = updatedRow["우선순위 설정"];
  const originalMetricImportance = getMetricImportanceValue(updatedRow["우선순위"])
  const isMetricDelete = updatedRow["삭제"];

  if (isMetricDelete == true){
    result.deleteMetricIds.push({
      metricId: metricId,
      metricName: metricName
    });

    return
  }
  if (modifiedMetricImportance != originalMetricImportance){
    // error check
    if (modifiedMetricImportance >= 1 && modifiedMetricImportance <= 5){
      result.modifyMetrics.push({
        metricId: metricId,
        metricName: metricName,
        newImportance: modifiedMetricImportance,
        oldImportance: originalMetricImportance
      });
    }else{
      utils.showNotification({
        title: "우선순위 설정 오류",
        description: `${metricName} 의 우선순위를 1~5 사이로 설정해주세요. 낮은 숫자일수록 더 높은 우선순위를 가집니다. 현재 설정값 : ${originalMetricImportance}`,
        notificationType: "error",
        duration: 4
      });
    }
  }
}

