function getMetricImportanceValue(importanceString){
  if(importanceString == "VERY_LOW") return 5;
  else if(importanceString == "LOW") return 4;
  else if(importanceString == "MEDIUM") return 3;
  else if(importanceString == "HIGH") return 2;
  else if(importanceString == "VERY_HIGH") return 1;
}



const arrayColor = [
  "#FF0000",
