function getPlotly(name, x, y, counter){
  var value = {
    "name": name,
    "x": x,
    "y": y,
    "type": 'line',
    "hovertemplate": "<b>%{x}</b><br>%{fullData.name}: %{y}<extra></extra>",
    "transforms": [
      {
        "type": "sort",
        "target": x,
        "order": "ascending"
      },
      {
        "type": "aggregate",
        "groups": x,
        "aggregations": [
          {
            "target": "y",
            "func": "sum",
            "enabled": true
          }
        ]
      }
    ],
    "marker": {
      "color": arrayColor[counter]
    },
    "mode": "lines+markers"
  }
  if (counter > 0 ) value["yaxis"] = `y${counter + 1}`
  return value;
}

function addYAxis(name, counter){
  var value = {
    "title": {
      "text": name,
      "standoff": 6,
      "font": {
        "size": 12
      }
    },
    "titlefont": {color : arrayColor[counter]},
    "tickfont": {color : arrayColor[counter]},
    "type": "linear",
    "zerolinecolor": arrayColor[counter]
  }
  if (counter > 0) {
    value["side"] = "left",
    value["anchor"] = "free",
    value["position"] = 0.08 * counter
    value["overlaying"] = "y"
  }
  return value;
}

var chartData = []
var viewInfoData = {}
var queryExecute = []
await new Promise((resolve) => {
  GetProductHistoryPrice.trigger({
    onSuccess: (data) => {
      chartData.push(getPlotly("상품 가격", data.datetime, data.values, 0))
      viewInfoData["yaxis"] = addYAxis("상품 가격", 0)
      resolve();
    }
  })
})

var metrics = []
localStorage.values.metrics.forEach((metricInfo, index) => {
  queryExecute.push(new Promise((resolve) => {
    GetMetricHistory.trigger({
      additionalScope: {
        metricId: metricInfo.metric.id
      },
      onSuccess: (data) => {
        if (data.datetime.length > 0){
          console.log(index + 1);
          var datetimes = data.datetime
          var values = data.values
          chartData.push(getPlotly(metricInfo.metric.name, datetimes, values, index + 1))
          viewInfoData[axisName[index + 1]] = addYAxis(metricInfo.metric.name, index + 1)
          metrics.push({"metricName": metricInfo.metric.name, "value": values[0], "importance": getMetricImportanceValue(metricInfo.importance), "nickname": metricInfo.metric.nickname})
        }
        resolve();
      },
      onFailure: (data) => {
        resolve();
      }
    })
  }))
})
await Promise.all(queryExecute)

localStorage.setValue("productMetricsLatest", metrics)

getMetricStatus.trigger({
  additionalScope: {
    metrics: metrics
  }
});

return [chartData, viewInfoData];

